sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// [GOOD] The NeedIt Tasks should not have a State field value of Closed Complete.
// Query for NeedIt requests that are due between now and 24 hrs from now.
var dueSoon = new GlideRecord('x_58872_needit_needit_task');

// Instead of creating more date objects, just crunch the condition checking inside addQuery.
// -24 hours ago is 24 hours in the future.
dueSoon.addQuery('due_date','>=',rightNow);
dueSoon.addQuery('due_date','<',gs.hoursAgo(-24));
// Only return NeedIt Task records that do not have a State field value of Closed Complete.
dueSoon.addQuery('state', '!=', 3);

dueSoon.query();
// Write a log message for each overdue NeedIt Task Record
while (dueSoon.next()) {
    gs.info("NeedIt Task due soon record = " + dueSoon.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTask',dueSoon,dueSoon.number,gs.getUserName());
}
